(function() {
    var restServer = new FakeRest.Server();
    restServer.init({
        'articles': [
            { id: 0, title: 'Welcome', body: 'You are new' },
            { id: 1, title: 'Policies', body: 'There is no rule' }
        ],
        'comments': [
            { id: 0, article_id: 0, body: 'Well written!' },
            { id: 1, article_id: 0, body: 'Nice article.' },
            { id: 2, article_id: 1, body: 'Love it!' },
            { id: 3, article_id: 1, body: 'Why?' }
        ]
    });

    // use sinon.js to monkey-patch XmlHttpRequest
    var server = sinon.fakeServer.create();
    server.respondWith(restServer.getHandler());
}());
